import React, { Component } from "react";
import HeaderNav from "../components/HeaderNav";
import PageTitle from "../components/PageTitle";
import CircleImage from "../components/CircleImage";

class Home extends Component {
  state = {
    profileDisplay: false
  };

  render() {
    return (
      <div>
        <HeaderNav selected={"Home"} />
        <section className="page home-page flex-column">
          <div className="about">
            <CircleImage src="./images/me.jpg" circle={true}/>
            <p>
              Hello, my name is Patrick Gerspach and welcome to my portfolio
              site! I am an aspiring web developer/software engineer who has
              completely changed the course of his life in pursuit of a more
              fulfilling and enjoyable career.
            </p>
            <p>
              I began coding at the University of Notre Dame, where I studied
              mechanical engineering. My experience started with learning the
              basics of an archaic programming language called Fortran. From
              there, pieces of coding began to slip into my coursework through
              MatLAB and C, and it didn't take me long to desire to learn more.
            </p>
            <p>
              Shortly after finishing my degree and beginning work, I signed up
              for the Columbia University Engineering Coding Boot Camp, from
              which I am set to graduate at the end March. I have learned an
              incredible amount in such a short time, and my interest has only
              grown since the onset of my journey. I hope you enjoy browsing my
              projects as much as I enjoyed working on them!
            </p>
          </div>
        </section>
      </div>
    );
  }
}
export default Home;
