import React, { Component } from "react";
import ProfileSnapshot from "./ProfileSnapshot";

class HeaderNav extends Component {
  state = {
    links: ["History", "Skillset", "Interests", "Projects", "Future Projects"],
    profileDisplay: false

  };
  handleOnProfileSelect = event => {
    this.setState({
      profileDisplay: !this.state.profileDisplay
    });
  };
  render() {
    const pStyle = {
      width: `calc(100vw / ${this.state.links.length + 1})`,
      fontSize: "24px",
      borderRight: "solid 1px var(--color1)"
    };
    const aStyle = {
      width: `calc(100vw / ${this.state.links.length + 1})`
    };
    return (
      <div className="flex-column">
        <div className="flex-row header-nav">
          <div
            onMouseEnter={this.handleOnProfileSelect}
            onMouseLeave={this.handleOnProfileSelect}
            style={pStyle}
            className="flex-column"
          >
            <a             onMouseEnter={this.handleOnProfileSelect}
            onMouseLeave={this.handleOnProfileSelect}className = "pgerspach nav-link"href="#"><span>P. Gerspach</span></a>
            <ProfileSnapshot profileDisplay = {this.state.profileDisplay}/>
          </div>

          {this.state.links.map(linkName => (
            <a
              style={aStyle}
              className={`nav-link ${linkName
                .toLowerCase()
                .replace(/\s/, "-")}`}
              href="#"
            >
              <span>{linkName}</span>
            </a>
          ))}
        </div>
      </div>
    );
  }
}
export default HeaderNav;
