import React, { Component } from "react";
import CircleImage from "./CircleImage";

class Project extends Component {
  render() {
    return (
      <div className="project" style={{boxShadow:"0 0 3px 0px black",padding:"10px", marginBottom:"30px"}}>
        <div className="project-title" style={{fontSize:"32px",marginBottom:"10px"}}>
          <span>{this.props.name}</span>
        </div>
        <div className="project-body">
          <img src={this.props.image}style={{width:"285px",height:"170px", border:"1px solid var(--color5)", borderRadius:"10px"}} />
          <div className="project-description">{this.props.description.split("|").map(par=>(
              <p>{par.trim()}</p>
          ))}</div>
        </div>
      </div>
    );
  }
}
export default Project;
