import React, { Component } from "react";
import HeaderNav from "../components/HeaderNav";
import HeaderNavMobile from "../components/HeaderNavMobile";

import CircleImage from "../components/CircleImage";
import IconRow from "../components/IconRow";

class Home extends Component {
  state = {
    profileDisplay: false,
    mobile: false
  };
  componentWillMount() {
    this.setState({
      mobile: !(window.innerWidth > 400)
    });
  }
  render() {
    const icons = ["ndrecruit", "quote", "fborm"];
    return (
      <div>
        {!this.state.mobile ? (
          <HeaderNav selected={"Home"} />
        ) : (
          <HeaderNavMobile selected={"Home"} />
        )}
        <section className="page home-page flex-column">
          <div
            style={{ alignItems: "center", justifyContent: "flex-end" }}
            className="about flex-column"
          >
            <CircleImage
              imageId="headshot"
              imageSource="./images/me.jpg"
              size="200px"
              circle={true}
            />
            <p style={{ margin: "0", marginTop: "70px", width:"800px", alignSelf:"flex-start"}}>
              Hello, my name is Patrick Gerspach and welcome to my website! I'm
              a full stack developer with a deep love for programming. I'm at my
              happiest when I'm solving mind-bending coding problems, hacking
              away on a project, or helping others set up their personal
              websites for the world to see!
            </p>
          </div>
        </section>
        <div
          className={`about-passion-wrap ${
            this.state.mobile ? "flex-column" : "flex-row"
          }`}
        >
          <div
            className={`about-passion-side right${
              this.state.mobile ? "flex-row" : "flex-column"
            }`}
          >
            <div className={`about-passion-icon-wrap`}>
              <IconRow
                jpg={false}
                numRows={2}
                size={"160"}
                radius={"5"}
                circle={false}
                icons={icons}
                shadow={5}
              />
            </div>
          </div>
          <div
            className={`about-passion-side ${
              this.state.mobile ? "flex-row" : "flex-column"
            }`}
          >
            <p>
              With experience in multiple languages and frameworks spanning from
              front end to back end, I am capable of building full web
              applications with all the bells and whistles your heart desires.
            </p>
          </div>
        </div>
      </div>
    );
  }
}
export default Home;
